import React from "react";
import TodoList from "./todo/TodoList";

function App() {
  const [todos, setTodos] = React.useState([
    {id: 1, completed:false, title: "Купить колбасы"},
    {id: 2, completed:true, title: "Купить творога"},
    {id: 3, completed:false, title: "Купить груш"}
  ])

  function toggleTodo(id){
    setTodos(
      todos.map(todo => {
        if (todo.id === id){ 
          todo.completed = !todo.completed
        }
        return todo
      })
    )
  }

  return (
    (<div className="wrapper">
      <h1>React Tutorial</h1>
      <TodoList 
        todos={todos} 
        onToggle={toggleTodo} 
      />
    </div>)
  );
}

export default App;
